get-tags.map:
function (doc) {
  if (doc.docType && doc.docType === "TAG"
) {
    for (var cardIndex in doc.taggedCards){
      emit(doc.taggedCards[cardIndex], {
        docType: doc.docType,
        name: doc.name,
        snippit: doc.snippit,
        wiki: '',
        taggedCards: []
      });
    }
  }
}
